func AM_activateNewScale() {
	m_x += m_w / 2
	m_y += m_h / 2
	m_w = FixedMul(f_w<<16, scale_ftom)
	m_h = FixedMul(f_h<<16, scale_ftom)
	m_x -= m_w / 2
	m_y -= m_h / 2
	m_x2 = m_x + m_w
	m_y2 = m_y + m_h
}

// C documentation
//
//	//
//	//
//	//
func AM_saveScaleAndLoc() {
	old_m_x = m_x
	old_m_y = m_y
	old_m_w = m_w
	old_m_h = m_h
}

// C documentation
//
//	//
//	//
//	//
func AM_restoreScaleAndLoc() {
	m_w = old_m_w
	m_h = old_m_h
	if !(followplayer != 0) {
		m_x = old_m_x
		m_y = old_m_y
	} else {
		m_x = (*mobj_t)(unsafe.Pointer(plr.Fmo)).Fx - m_w/int32(2)
		m_y = (*mobj_t)(unsafe.Pointer(plr.Fmo)).Fy - m_h/int32(2)
	}
	m_x2 = m_x + m_w
	m_y2 = m_y + m_h
	// Change the scaling multipliers
	scale_mtof = FixedDiv(f_w<<int32(FRACBITS), m_w)
	scale_ftom = FixedDiv(1<<FRACBITS, scale_mtof)
}

