func AM_initVariables(tls *libc.TLS) {
	var v1 fixed_t
	automapactive = 1
	fb = I_VideoBuffer
	f_oldloc.Fx = int32(INT_MAX1)
	lightlev = 0
	v1 = 0
	m_paninc.Fy = v1
	m_paninc.Fx = v1
	ftom_zoommul = 1 << FRACBITS
	mtof_zoommul = 1 << FRACBITS
	m_w = FixedMul(f_w<<16, scale_ftom)
	m_h = FixedMul(f_h<<16, scale_ftom)
	// find player to center on initially
	if playeringame[consoleplayer] != 0 {
		plr = &players[consoleplayer]
	} else {
		plr = &players[0]
		for pnum := 0; pnum < MAXPLAYERS; pnum++ {
			if playeringame[pnum] != 0 {
				plr = &players[pnum]
				break
			}
		}
	}
	m_x = (*mobj_t)(unsafe.Pointer(plr.Fmo)).Fx - m_w/int32(2)
	m_y = (*mobj_t)(unsafe.Pointer(plr.Fmo)).Fy - m_h/int32(2)
	AM_changeWindowLoc()
	// for saving & restoring
	old_m_x = m_x
	old_m_y = m_y
	old_m_w = m_w
	old_m_h = m_h
	// inform the status bar of the change
	ST_Responder(tls, &st_notify)
}

