// C documentation
//
//	//
//	// Automap clipping of lines.
//	//
//	// Based on Cohen-Sutherland clipping algorithm but with a slightly
//	// faster reject and precalculated slopes.  If the speed is needed,
//	// use a hash algorithm to handle  the common cases.
//	//
func AM_clipMline(ml *mline_t, fl *fline_t) (r boolean) {
	var dx, dy, outcode1, outcode2, outside int32
	var tmp fpoint_t
	outcode1 = 0
	outcode2 = 0
	// do trivial rejects and outcodes
	if ml.Fa.Fy > m_y2 {
		outcode1 = 8
	} else {
		if ml.Fa.Fy < m_y {
			outcode1 = 4
		}
	}
	if ml.Fb.Fy > m_y2 {
		outcode2 = 8
	} else {
		if ml.Fb.Fy < m_y {
			outcode2 = 4
		}
	}
	if outcode1&outcode2 != 0 {
		return 0
	} // trivially outside
	if ml.Fa.Fx < m_x {
		outcode1 |= 1
	} else {
		if ml.Fa.Fx > m_x2 {
			outcode1 |= 2
		}
	}
	if ml.Fb.Fx < m_x {
		outcode2 |= 1
	} else {
		if ml.Fb.Fx > m_x2 {
			outcode2 |= 2
		}
	}
	if outcode1&outcode2 != 0 {
		return 0
	} // trivially outside
	// transform to frame-buffer coordinates.
	fl.Fa.Fx = f_x + FixedMul(ml.Fa.Fx-m_x, scale_mtof)>>16
	fl.Fa.Fy = f_y + (f_h - FixedMul(ml.Fa.Fy-m_y, scale_mtof)>>16)
	fl.Fb.Fx = f_x + FixedMul(ml.Fb.Fx-m_x, scale_mtof)>>16
	fl.Fb.Fy = f_y + (f_h - FixedMul(ml.Fb.Fy-m_y, scale_mtof)>>16)
	outcode1 = 0
	if fl.Fa.Fy < 0 {
		outcode1 |= 8
	} else {
		if fl.Fa.Fy >= f_h {
			outcode1 |= 4
		}
	}
	if fl.Fa.Fx < 0 {
		outcode1 |= 1
	} else {
		if fl.Fa.Fx >= f_w {
			outcode1 |= 2
		}
	}
	outcode2 = 0
	if fl.Fb.Fy < 0 {
		outcode2 |= 8
	} else {
		if fl.Fb.Fy >= f_h {
			outcode2 |= 4
		}
	}
	if fl.Fb.Fx < 0 {
		outcode2 |= 1
	} else {
		if fl.Fb.Fx >= f_w {
			outcode2 |= 2
		}
	}
	if outcode1&outcode2 != 0 {
		return 0
	}
	for outcode1|outcode2 != 0 {
		// may be partially inside box
		// find an outside point
		if outcode1 != 0 {
			outside = outcode1
		} else {
			outside = outcode2
		}
		// clip to each side
		if outside&8 != 0 {
			dy = fl.Fa.Fy - fl.Fb.Fy
			dx = fl.Fb.Fx - fl.Fa.Fx
			tmp.Fx = fl.Fa.Fx + dx*fl.Fa.Fy/dy
			tmp.Fy = 0
		} else {
			if outside&4 != 0 {
				dy = fl.Fa.Fy - fl.Fb.Fy
				dx = fl.Fb.Fx - fl.Fa.Fx
				tmp.Fx = fl.Fa.Fx + dx*(fl.Fa.Fy-f_h)/dy
				tmp.Fy = f_h - 1
			} else {
				if outside&2 != 0 {
					dy = fl.Fb.Fy - fl.Fa.Fy
					dx = fl.Fb.Fx - fl.Fa.Fx
					tmp.Fy = fl.Fa.Fy + dy*(f_w-1-fl.Fa.Fx)/dx
					tmp.Fx = f_w - 1
				} else {
					if outside&1 != 0 {
						dy = fl.Fb.Fy - fl.Fa.Fy
						dx = fl.Fb.Fx - fl.Fa.Fx
						tmp.Fy = fl.Fa.Fy + dy*-fl.Fa.Fx/dx
						tmp.Fx = 0
					} else {
						tmp.Fx = 0
						tmp.Fy = 0
					}
				}
			}
		}
		if outside == outcode1 {
			fl.Fa = tmp
			outcode1 = 0
			if fl.Fa.Fy < 0 {
				outcode1 |= 8
			} else {
				if fl.Fa.Fy >= f_h {
					outcode1 |= 4
				}
			}
			if fl.Fa.Fx < 0 {
				outcode1 |= 1
			} else {
				if fl.Fa.Fx >= f_w {
					outcode1 |= 2
				}
			}
		} else {
			fl.Fb = tmp
			outcode2 = 0
			if fl.Fb.Fy < 0 {
				outcode2 |= 8
			} else {
				if fl.Fb.Fy >= f_h {
					outcode2 |= 4
				}
			}
			if fl.Fb.Fx < 0 {
				outcode2 |= 1
			} else {
				if fl.Fb.Fx >= f_w {
					outcode2 |= 2
				}
			}
		}
		if outcode1&outcode2 != 0 {
			return 0
		} // trivially outside
	}
	return 1
}

