func AM_drawFline(fl *fline_t, color int32) {
	var ax, ay, d, dx, dy, sx, sy, x, y, v1, v2, v3, v4, v5 int32
	// For debugging only
	if fl.Fa.Fx < 0 || fl.Fa.Fx >= f_w || fl.Fa.Fy < 0 || fl.Fa.Fy >= f_h || fl.Fb.Fx < 0 || fl.Fb.Fx >= f_w || fl.Fb.Fy < 0 || fl.Fb.Fy >= f_h {
		v1 = fuck
		fuck++

		fprintf_ccgo(os.Stderr, 93, v1)
		return
	}
	dx = fl.Fb.Fx - fl.Fa.Fx
	if dx < 0 {
		v2 = -dx
	} else {
		v2 = dx
	}
	ax = 2 * v2
	if dx < 0 {
		v3 = -1
	} else {
		v3 = 1
	}
	sx = v3
	dy = fl.Fb.Fy - fl.Fa.Fy
	if dy < 0 {
		v4 = -dy
	} else {
		v4 = dy
	}
	ay = 2 * v4
	if dy < 0 {
		v5 = -1
	} else {
		v5 = 1
	}
	sy = v5
	x = fl.Fa.Fx
	y = fl.Fa.Fy
	if ax > ay {
		d = ay - ax/int32(2)
		for 1 != 0 {
			*(*uint8)(unsafe.Pointer(fb + uintptr(y*f_w+x))) = libc.Uint8FromInt32(color)
			if x == fl.Fb.Fx {
				return
			}
			if d >= 0 {
				y += sy
				d -= ax
			}
			x += sx
			d += ay
		}
	} else {
		d = ax - ay/int32(2)
		for 1 != 0 {
			*(*uint8)(unsafe.Pointer(fb + uintptr(y*f_w+x))) = libc.Uint8FromInt32(color)
			if y == fl.Fb.Fy {
				return
			}
			if d >= 0 {
				x += sx
				d -= ay
			}
			y += sy
			d += ax
		}
	}
}

