func D_ProcessEvents(tls *libc.TLS) {
	// IF STORE DEMO, DO NOT ACCEPT INPUT
	if storedemo != 0 {
		return
	}
	for {
		ev := D_PopEvent()
		if ev == nil {
			break
		}
		if M_Responder(tls, ev) != 0 {
			continue
		} // menu ate the event
		G_Responder(tls, ev)
	}
}

