//	//
//	// This cycles through the demo sequences.
//	// FIXME - version dependend demo numbers?
//	//
func D_DoAdvanceDemo(tls *libc.TLS) {
	players[consoleplayer].Fplayerstate = int32(PST_LIVE) // not reborn
	advancedemo = 0
	usergame = 0 // no save / end game here
	paused = 0
	gameaction = ga_nothing
	// The Ultimate Doom executable changed the demo sequence to add
	// a DEMO4 demo.  Final Doom was based on Ultimate, so also
	// includes this change; however, the Final Doom IWADs do not
	// include a DEMO4 lump, so the game bombs out with an error
	// when it reaches this point in the demo sequence.
	// However! There is an alternate version of Final Doom that
	// includes a fixed executable.
	if gameversion == exe_ultimate || gameversion == exe_final {
		demosequence = (demosequence + 1) % 7
	} else {
		demosequence = (demosequence + 1) % 6
	}
	switch demosequence {
	case 0:
		if gamemode == commercial {
			pagetic = TICRATE * 11
		} else {
			pagetic = 170
		}
		gamestate = GS_DEMOSCREEN
		pagename = __ccgo_ts(1896)
		if gamemode == commercial {
			S_StartMusic(tls, int32(mus_dm2ttl))
		} else {
			S_StartMusic(tls, int32(mus_intro))
		}
	case 1:
		G_DeferedPlayDemo(__ccgo_ts(1905))
	case 2:
		pagetic = 200
		gamestate = GS_DEMOSCREEN
		pagename = __ccgo_ts(1911)
	case 3:
		G_DeferedPlayDemo(__ccgo_ts(1918))
	case 4:
		gamestate = GS_DEMOSCREEN
		if gamemode == commercial {
			pagetic = TICRATE * 11
			pagename = __ccgo_ts(1896)
			S_StartMusic(tls, int32(mus_dm2ttl))
		} else {
			pagetic = 200
			if gamemode == retail {
				pagename = __ccgo_ts(1911)
			} else {
				pagename = __ccgo_ts(1924)
			}
		}
	case 5:
		G_DeferedPlayDemo(__ccgo_ts(1930))
		break
		// THE DEFINITIVE DOOM Special Edition demo
		fallthrough
	case 6:
		G_DeferedPlayDemo(__ccgo_ts(1936))
		break
	}
	// The Doom 3: BFG Edition version of doom2.wad does not have a
	// TITLETPIC lump. Use INTERPIC instead as a workaround.
	if bfgedition != 0 && !(xstrcasecmp(pagename, __ccgo_ts(1896)) != 0) && W_CheckNumForName(__ccgo_ts(1942)) < 0 {
		pagename = __ccgo_ts(1951)
	}
}

