// C documentation
//
//	// sorta called by HU_Erase and just better darn get things straight
func HUlib_eraseTextLine(l *hu_textline_t) {
	var lh, y, yoffset int32
	// Only erases when NOT in automap and the screen is reduced,
	// and the text must either need updating or refreshing
	// (because of a recent change back from the automap)
	if !(automapactive != 0) && viewwindowx != 0 && l.Fneedsupdate != 0 {
		lh = int32((*patch_t)(unsafe.Pointer(*(*uintptr)(unsafe.Pointer(l.Ff)))).Fheight) + 1
		y = l.Fy
		yoffset = y * SCREENWIDTH
		for {
			if !(y < l.Fy+lh) {
				break
			}
			if y < viewwindowy || y >= viewwindowy+viewheight {
				R_VideoErase(libc.Uint32FromInt32(yoffset), int32(SCREENWIDTH))
			} else {
				R_VideoErase(libc.Uint32FromInt32(yoffset), viewwindowx) // erase left border
				R_VideoErase(libc.Uint32FromInt32(yoffset+viewwindowx+viewwidth), viewwindowx)
				// erase right border
			}
			goto _1
		_1:
			;
			y++
			yoffset += int32(SCREENWIDTH)
		}
	}
	if l.Fneedsupdate != 0 {
		l.Fneedsupdate--
	}
}

