func CheckVolumeSeparation(vol *int32, sep *int32) {
	if *sep < 0 {
		*sep = 0
	} else {
		if *sep > 254 {
			*sep = 254
		}
	}
	if *vol < 0 {
		*vol = 0
	} else {
		if *vol > 127 {
			*vol = 127
		}
	}
}

func I_UpdateSoundParams(channel int32, vol int32, sep int32) {
	if sound_module != nil {
		CheckVolumeSeparation(&vol, &sep)
		sound_module.FUpdateSoundParams(channel, vol, sep)
	}
}

