//
// FixedDiv, C version.
//

func FixedDiv(a fixed_t, b fixed_t) (r fixed_t) {
	var result int64
	var v1 int32
	if xabs(a)>>int32(14) >= xabs(b) {
		if a^b < 0 {
			v1 = -1 - 0x7fffffff
		} else {
			v1 = int32(INT_MAX7)
		}
		return v1
	} else {
		result = int64(a) << 16 / int64(b)
		return int32(result)
	}
	return r
}

