func P_RecursiveSound(tls *libc.TLS, sec *sector_t, soundblocks int32) {
	var check *line_t
	var other *sector_t
	var i int32
	// wake up all monsters in this sector
	if sec.Fvalidcount == validcount && sec.Fsoundtraversed <= soundblocks+int32(1) {
		return // already flooded
	}
	sec.Fvalidcount = validcount
	sec.Fsoundtraversed = soundblocks + 1
	sec.Fsoundtarget = soundtarget
	i = 0
	for {
		if !(i < sec.Flinecount) {
			break
		}
		check = sec.Flines[i]
		if !(int32(check.Fflags)&ML_TWOSIDED != 0) {
			goto _1
		}
		P_LineOpening(check)
		if openrange <= 0 {
			goto _1
		} // closed door
		if (*side_t)(unsafe.Pointer(sides+uintptr(check.Fsidenum[0])*24)).Fsector == sec {
			other = (*side_t)(unsafe.Pointer(sides + uintptr(check.Fsidenum[1])*24)).Fsector
		} else {
			other = (*side_t)(unsafe.Pointer(sides + uintptr(check.Fsidenum[0])*24)).Fsector
		}
		if int32(check.Fflags)&ML_SOUNDBLOCK != 0 {
			if !(soundblocks != 0) {
				P_RecursiveSound(tls, other, 1)
			}
		} else {
			P_RecursiveSound(tls, other, soundblocks)
		}
		goto _1
	_1:
		;
		i++
	}
}

