// Check whether the death of the specified monster type is allowed
// to trigger the end of episode special action.
//
// This behavior changed in v1.9, the most notable effect of which
// was to break uac_dead.wad

func CheckBossEnd(tls *libc.TLS, motype mobjtype_t) (r boolean) {
	if gameversion < exe_ultimate {
		if gamemap != 8 {
			return 0
		}
		// Baron death on later episodes is nothing special.
		if motype == int32(MT_BRUISER) && gameepisode != 1 {
			return 0
		}
		return 1
	} else {
		// New logic that appeared in Ultimate Doom.
		// Looks like the logic was overhauled while adding in the
		// episode 4 support.  Now bosses only trigger on their
		// specific episode.
		switch gameepisode {
		case 1:
			return libc.BoolUint32(gamemap == 8 && motype == int32(MT_BRUISER))
		case 2:
			return libc.BoolUint32(gamemap == 8 && motype == int32(MT_CYBORG))
		case 3:
			return libc.BoolUint32(gamemap == 8 && motype == int32(MT_SPIDER))
		case 4:
			return libc.BoolUint32(gamemap == 6 && motype == int32(MT_CYBORG) || gamemap == 8 && motype == int32(MT_SPIDER))
		default:
			return libc.BoolUint32(gamemap == 8)
		}
	}
	return r
}

// C documentation
//
//	//
//	// A_BossDeath
//	// Possibly trigger special effects
//	// if on first boss level
//	//
func A_BossDeath(tls *libc.TLS, mo uintptr) {
	var i int32
	var mo2, th uintptr
	if gamemode == commercial {
		if gamemap != 7 {
			return
		}
		if (*mobj_t)(unsafe.Pointer(mo)).Ftype1 != int32(MT_FATSO) && (*mobj_t)(unsafe.Pointer(mo)).Ftype1 != int32(MT_BABY) {
			return
		}
	} else {
		if !(CheckBossEnd(tls, (*mobj_t)(unsafe.Pointer(mo)).Ftype1) != 0) {
			return
		}
	}
	// make sure there is a player alive for victory
	i = 0
	for {
		if !(i < int32(MAXPLAYERS)) {
			break
		}
		if playeringame[i] != 0 && players[i].Fhealth > 0 {
			break
		}
		goto _1
	_1:
		;
		i++
	}
	if i == int32(MAXPLAYERS) {
		return
	} // no one left alive, so do not end game
	// scan the remaining thinkers to see
	// if all bosses are dead
	th = thinkercap.Fnext
	for {
		if !(th != uintptr(unsafe.Pointer(&thinkercap))) {
			break
		}
		if *(*actionf_p1)(unsafe.Pointer(th + 16)) != __ccgo_fp(P_MobjThinker) {
			goto _2
		}
		mo2 = th
		if mo2 != mo && (*mobj_t)(unsafe.Pointer(mo2)).Ftype1 == (*mobj_t)(unsafe.Pointer(mo)).Ftype1 && (*mobj_t)(unsafe.Pointer(mo2)).Fhealth > 0 {
			// other boss not dead
			return
		}
		goto _2
	_2:
		;
		th = (*thinker_t)(unsafe.Pointer(th)).Fnext
	}
	// victory!
	if gamemode == commercial {
		if gamemap == 7 {
			if (*mobj_t)(unsafe.Pointer(mo)).Ftype1 == int32(MT_FATSO) {
				EV_DoFloor(tls, &line_t{Ftag: 666}, int32(lowerFloorToLowest))
				return
			}
			if (*mobj_t)(unsafe.Pointer(mo)).Ftype1 == int32(MT_BABY) {
				EV_DoFloor(tls, &line_t{Ftag: 667}, int32(raiseToTexture))
				return
			}
		}
	} else {
		switch gameepisode {
		case 1:
			EV_DoFloor(tls, &line_t{Ftag: 666}, int32(lowerFloorToLowest))
			return
		case 4:
			switch gamemap {
			case 6:
				EV_DoDoor(tls, &line_t{Ftag: 666}, int32(vld_blazeOpen))
				return
			case 8:
				EV_DoFloor(tls, &line_t{Ftag: 666}, int32(lowerFloorToLowest))
				return
				break
			}
		}
	}
	G_ExitLevel()
}

