func P_GiveBody(player *player_t, num int32) (r boolean) {
	if player.Fhealth >= int32(MAXHEALTH) {
		return 0
	}
	player.Fhealth += num
	if player.Fhealth > int32(MAXHEALTH) {
		player.Fhealth = int32(MAXHEALTH)
	}
	(*mobj_t)(unsafe.Pointer(player.Fmo)).Fhealth = player.Fhealth
	return 1
}

