func P_GiveArmor(tls *libc.TLS, player *player_t, armortype int32) (r boolean) {
	var hits int32
	hits = armortype * 100
	if player.Farmorpoints >= hits {
		return 0
	} // don't pick up
	player.Farmortype = armortype
	player.Farmorpoints = hits
	return 1
}

