// C documentation
//
//	//
//	// PIT_CheckLine
//	// Adjusts tmfloorz and tmceilingz as lines are contacted
//	//
func PIT_CheckLine(tls *libc.TLS, ld *line_t) (r boolean) {
	if tmbbox[int32(BOXRIGHT)] <= ld.Fbbox[BOXLEFT] || tmbbox[int32(BOXLEFT)] >= ld.Fbbox[BOXRIGHT] || tmbbox[int32(BOXTOP)] <= ld.Fbbox[BOXBOTTOM] || tmbbox[int32(BOXBOTTOM)] >= ld.Fbbox[BOXTOP] {
		return 1
	}
	if P_BoxOnLineSide(uintptr(unsafe.Pointer(&tmbbox)), ld) != -1 {
		return 1
	}
	// A line has been hit
	// The moving thing's destination position will cross
	// the given line.
	// If this should not be allowed, return false.
	// If the line is special, keep track of it
	// to process later if the move is proven ok.
	// NOTE: specials are NOT sorted by order,
	// so two special lines that are only 8 pixels apart
	// could be crossed in either order.
	if !(ld.Fbacksector != nil) {
		return 0
	} // one sided line
	if !((*mobj_t)(unsafe.Pointer(tmthing)).Fflags&int32(MF_MISSILE) != 0) {
		if int32(ld.Fflags)&ML_BLOCKING != 0 {
			return 0
		} // explicitly blocking everything
		if !((*mobj_t)(unsafe.Pointer(tmthing)).Fplayer != nil) && int32(ld.Fflags)&ML_BLOCKMONSTERS != 0 {
			return 0
		} // block monsters only
	}
	// set openrange, opentop, openbottom
	P_LineOpening(ld)
	// adjust floor / ceiling heights
	if opentop < tmceilingz {
		tmceilingz = opentop
		ceilingline = ld
	}
	if openbottom > tmfloorz {
		tmfloorz = openbottom
	}
	if lowfloor < tmdropoffz {
		tmdropoffz = lowfloor
	}
	// if contacted a special line, add it to the list
	if ld.Fspecial != 0 {
		spechit[numspechit] = ld
		numspechit++
		// fraggle: spechits overrun emulation code from prboom-plus
		if numspechit > int32(MAXSPECIALCROSS_ORIGINAL) {
			SpechitOverrun(tls, ld)
		}
	}
	return 1
}

