//
// P_AproxDistance
// Gives an estimation of distance (not exact)
//

func P_AproxDistance(dx fixed_t, dy fixed_t) (r fixed_t) {
	dx = xabs(dx)
	dy = xabs(dy)
	if dx < dy {
		return dx + dy - dx>>1
	}
	return dx + dy - dy>>1
}

// C documentation
//
//	//
//	// P_PointOnLineSide
//	// Returns 0 or 1
//	//
func P_PointOnLineSide(x fixed_t, y fixed_t, line *line_t) (r int32) {
	var dx, dy, left, right fixed_t
	if !(line.Fdx != 0) {
		if x <= line.Fv1.Fx {
			return libc.BoolInt32(line.Fdy > 0)
		}
		return libc.BoolInt32(line.Fdy < 0)
	}
	if !(line.Fdy != 0) {
		if y <= line.Fv1.Fy {
			return libc.BoolInt32(line.Fdx < 0)
		}
		return libc.BoolInt32(line.Fdx > 0)
	}
	dx = x - line.Fv1.Fx
	dy = y - line.Fv1.Fy
	left = FixedMul(line.Fdy>>int32(FRACBITS), dx)
	right = FixedMul(dy, line.Fdx>>int32(FRACBITS))
	if right < left {
		return 0
	} // front side
	return 1 // back side
}

// C documentation
//
//	//
//	// P_BoxOnLineSide
//	// Considers the line to be infinite
//	// Returns side 0 or 1, -1 if box crosses the line.
//	//
func P_BoxOnLineSide(tmbox uintptr, ld *line_t) (r int32) {
	var p1, p2 int32
	p1 = 0
	p2 = 0
	switch ld.Fslopetype {
	case ST_HORIZONTAL:
		p1 = libc.BoolInt32(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXTOP)*4)) > ld.Fv1.Fy)
		p2 = libc.BoolInt32(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXBOTTOM)*4)) > ld.Fv1.Fy)
		if ld.Fdx < 0 {
			p1 ^= 1
			p2 ^= 1
		}
	case ST_VERTICAL:
		p1 = libc.BoolInt32(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXRIGHT)*4)) < ld.Fv1.Fx)
		p2 = libc.BoolInt32(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXLEFT)*4)) < ld.Fv1.Fx)
		if ld.Fdy < 0 {
			p1 ^= 1
			p2 ^= 1
		}
	case ST_POSITIVE:
		p1 = P_PointOnLineSide(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXLEFT)*4)), *(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXTOP)*4)), ld)
		p2 = P_PointOnLineSide(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXRIGHT)*4)), *(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXBOTTOM)*4)), ld)
	case ST_NEGATIVE:
		p1 = P_PointOnLineSide(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXRIGHT)*4)), *(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXTOP)*4)), ld)
		p2 = P_PointOnLineSide(*(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXLEFT)*4)), *(*fixed_t)(unsafe.Pointer(tmbox + uintptr(BOXBOTTOM)*4)), ld)
		break
	}
	if p1 == p2 {
		return p1
	}
	return -1
}

