// Pad the REJECT lump with extra data when the lump is too small,
// to simulate a REJECT buffer overflow in Vanilla Doom.

func PadRejectArray(array uintptr, len1 uint32) {
	var byte_num, i uint32
	var padvalue uint8
	var dest uintptr
	var rejectpad [4]uint32
	// Values to pad the REJECT array with:
	rejectpad = [4]uint32{
		0: libc.Uint32FromInt32((totallines*int32(4)+int32(3)) & ^3 + 24),
		2: uint32(50),
		3: uint32(0x1d4a11),
	}
	// Copy values from rejectpad into the destination array.
	dest = array
	i = uint32(0)
	for {
		if !(i < len1 && uint64(i) < uint64(16)) {
			break
		}
		byte_num = i % uint32(4)
		*(*uint8)(unsafe.Pointer(dest)) = uint8(rejectpad[i/uint32(4)] >> (byte_num * uint32(8)) & uint32(0xff))
		dest++
		goto _1
	_1:
		;
		i++
	}
	// We only have a limited pad size.  Print a warning if the
	// REJECT lump is too small.
	if uint64(len1) > uint64(16) {
		fprintf_ccgo(os.Stderr, 25149, len1, 16)
		// Pad remaining space with 0 (or 0xff, if specified on command line).
		if M_CheckParm(__ccgo_ts(25206)) != 0 {
			padvalue = 0xff
		} else {
			padvalue = 0x00
		}
		xmemset(array+uintptr(16), padvalue, uint64(len1)-uint64(16))
	}
}

func P_LoadReject(tls *libc.TLS, lumpnum int32) {
	var lumplen, minlength int32
	// Calculate the size that the REJECT lump *should* be.
	minlength = (numsectors*numsectors + 7) / 8
	// If the lump meets the minimum length, it can be loaded directly.
	// Otherwise, we need to allocate a buffer of the correct size
	// and pad it with appropriate data.
	lumplen = W_LumpLength(tls, libc.Uint32FromInt32(lumpnum))
	if lumplen >= minlength {
		rejectmatrix = W_CacheLumpNum(tls, lumpnum, int32(PU_LEVEL))
	} else {
		rejectmatrix = Z_Malloc(tls, minlength, int32(PU_LEVEL), uintptr(unsafe.Pointer(&rejectmatrix)))
		W_ReadLump(tls, libc.Uint32FromInt32(lumpnum), rejectmatrix)
		PadRejectArray(rejectmatrix+uintptr(lumplen), libc.Uint32FromInt32(minlength-lumplen))
	}
}

