const NF_SUBSECTOR1 = 32768

// C documentation
//
//	//
//	// P_DivlineSide
//	// Returns side 0 (front), 1 (back), or 2 (on).
//	//
func P_DivlineSide(tls *libc.TLS, x fixed_t, y fixed_t, node uintptr) (r int32) {
	var dx, dy, left, right fixed_t
	if !((*divline_t)(unsafe.Pointer(node)).Fdx != 0) {
		if x == (*divline_t)(unsafe.Pointer(node)).Fx {
			return 2
		}
		if x <= (*divline_t)(unsafe.Pointer(node)).Fx {
			return libc.BoolInt32((*divline_t)(unsafe.Pointer(node)).Fdy > 0)
		}
		return libc.BoolInt32((*divline_t)(unsafe.Pointer(node)).Fdy < 0)
	}
	if !((*divline_t)(unsafe.Pointer(node)).Fdy != 0) {
		if x == (*divline_t)(unsafe.Pointer(node)).Fy {
			return 2
		}
		if y <= (*divline_t)(unsafe.Pointer(node)).Fy {
			return libc.BoolInt32((*divline_t)(unsafe.Pointer(node)).Fdx < 0)
		}
		return libc.BoolInt32((*divline_t)(unsafe.Pointer(node)).Fdx > 0)
	}
	dx = x - (*divline_t)(unsafe.Pointer(node)).Fx
	dy = y - (*divline_t)(unsafe.Pointer(node)).Fy
	left = (*divline_t)(unsafe.Pointer(node)).Fdy >> FRACBITS * (dx >> FRACBITS)
	right = dy >> FRACBITS * ((*divline_t)(unsafe.Pointer(node)).Fdx >> FRACBITS)
	if right < left {
		return 0
	} // front side
	if left == right {
		return 2
	}
	return 1 // back side
}

// C documentation
//
//	//
//	// P_InterceptVector2
//	// Returns the fractional intercept point
//	// along the first divline.
//	// This is only called by the addthings and addlines traversers.
//	//
func P_InterceptVector2(tls *libc.TLS, v2 uintptr, v1 uintptr) (r fixed_t) {
	var den, frac, num fixed_t
	den = FixedMul((*divline_t)(unsafe.Pointer(v1)).Fdy>>int32(8), (*divline_t)(unsafe.Pointer(v2)).Fdx) - FixedMul((*divline_t)(unsafe.Pointer(v1)).Fdx>>int32(8), (*divline_t)(unsafe.Pointer(v2)).Fdy)
	if den == 0 {
		return 0
	}
	//	I_Error ("P_InterceptVector: parallel");
	num = FixedMul(((*divline_t)(unsafe.Pointer(v1)).Fx-(*divline_t)(unsafe.Pointer(v2)).Fx)>>int32(8), (*divline_t)(unsafe.Pointer(v1)).Fdy) + FixedMul(((*divline_t)(unsafe.Pointer(v2)).Fy-(*divline_t)(unsafe.Pointer(v1)).Fy)>>int32(8), (*divline_t)(unsafe.Pointer(v1)).Fdx)
	frac = FixedDiv(num, den)
	return frac
}

// C documentation
//
//	//
//	// P_CrossSubsector
//	// Returns true
//	//  if strace crosses the given subsector successfully.
//	//
func P_CrossSubsector(tls *libc.TLS, num int32) (r boolean) {
	bp := alloc(48)
	var seg uintptr
	var v1, v2 *vertex_t
	var line *line_t
	var front, back *sector_t
	var count, s1, s2 int32
	var frac, openbottom, opentop, slope fixed_t
	if num >= numsubsectors {
		I_Error(tls, __ccgo_ts(25239), num, numsubsectors)
	}
	sub := &subsectors[num]
	// check lines
	count = int32(sub.Fnumlines)
	seg = segs + uintptr(sub.Ffirstline)*56
	for {
		if !(count != 0) {
			break
		}
		line = (*seg_t)(unsafe.Pointer(seg)).Flinedef
		// allready checked other side?
		if line.Fvalidcount == validcount {
			goto _1
		}
		line.Fvalidcount = validcount
		v1 = line.Fv1
		v2 = line.Fv2
		s1 = P_DivlineSide(tls, v1.Fx, v1.Fy, uintptr(unsafe.Pointer(&strace)))
		s2 = P_DivlineSide(tls, v2.Fx, v2.Fy, uintptr(unsafe.Pointer(&strace)))
		// line isn't crossed?
		if s1 == s2 {
			goto _1
		}
		(*(*divline_t)(unsafe.Pointer(bp))).Fx = v1.Fx
		(*(*divline_t)(unsafe.Pointer(bp))).Fy = v1.Fy
		(*(*divline_t)(unsafe.Pointer(bp))).Fdx = v2.Fx - v1.Fx
		(*(*divline_t)(unsafe.Pointer(bp))).Fdy = v2.Fy - v1.Fy
		s1 = P_DivlineSide(tls, strace.Fx, strace.Fy, bp)
		s2 = P_DivlineSide(tls, t2x, t2y, bp)
		// line isn't crossed?
		if s1 == s2 {
			goto _1
		}
		// Backsector may be NULL if this is an "impassible
		// glass" hack line.
		if line.Fbacksector == nil {
			return 0
		}
		// stop because it is not two sided anyway
		// might do this after updating validcount?
		if !(int32(line.Fflags)&ML_TWOSIDED != 0) {
			return 0
		}
		// crosses a two sided line
		front = (*seg_t)(unsafe.Pointer(seg)).Ffrontsector
		back = (*seg_t)(unsafe.Pointer(seg)).Fbacksector
		// no wall to block sight with?
		if (*sector_t)(unsafe.Pointer(front)).Ffloorheight == (*sector_t)(unsafe.Pointer(back)).Ffloorheight && (*sector_t)(unsafe.Pointer(front)).Fceilingheight == (*sector_t)(unsafe.Pointer(back)).Fceilingheight {
			goto _1
		}
		// possible occluder
		// because of ceiling height differences
		if (*sector_t)(unsafe.Pointer(front)).Fceilingheight < (*sector_t)(unsafe.Pointer(back)).Fceilingheight {
			opentop = (*sector_t)(unsafe.Pointer(front)).Fceilingheight
		} else {
			opentop = (*sector_t)(unsafe.Pointer(back)).Fceilingheight
		}
		// because of ceiling height differences
		if (*sector_t)(unsafe.Pointer(front)).Ffloorheight > (*sector_t)(unsafe.Pointer(back)).Ffloorheight {
			openbottom = (*sector_t)(unsafe.Pointer(front)).Ffloorheight
		} else {
			openbottom = (*sector_t)(unsafe.Pointer(back)).Ffloorheight
		}
		// quick test for totally closed doors
		if openbottom >= opentop {
			return 0
		} // stop
		frac = P_InterceptVector2(tls, uintptr(unsafe.Pointer(&strace)), bp)
		if (*sector_t)(unsafe.Pointer(front)).Ffloorheight != (*sector_t)(unsafe.Pointer(back)).Ffloorheight {
			slope = FixedDiv(openbottom-sightzstart, frac)
			if slope > bottomslope {
				bottomslope = slope
			}
		}
		if (*sector_t)(unsafe.Pointer(front)).Fceilingheight != (*sector_t)(unsafe.Pointer(back)).Fceilingheight {
			slope = FixedDiv(opentop-sightzstart, frac)
			if slope < topslope {
				topslope = slope
			}
		}
		if topslope <= bottomslope {
			return 0
		} // stop
		goto _1
	_1:
		;
		seg += 56
		count--
	}
	// passed the subsector ok
	return 1
}

// C documentation
//
//	//
//	// P_CrossBSPNode
//	// Returns true
//	//  if strace crosses the given node successfully.
//	//
func P_CrossBSPNode(tls *libc.TLS, bspnum int32) (r boolean) {
	var side int32
	if bspnum&int32(NF_SUBSECTOR1) != 0 {
		if bspnum == -1 {
			return P_CrossSubsector(tls, 0)
		} else {
			return P_CrossSubsector(tls, bspnum & ^NF_SUBSECTOR1)
		}
	}
	bsp := uintptr(unsafe.Pointer(&nodes[bspnum]))
	// decide which side the start point is on
	side = P_DivlineSide(tls, strace.Fx, strace.Fy, bsp)
	if side == 2 {
		side = 0
	} // an "on" should cross both sides
	// cross the starting side
	if !(P_CrossBSPNode(tls, libc.Int32FromUint16(*(*uint16)(unsafe.Pointer(bsp + 48 + uintptr(side)*2)))) != 0) {
		return 0
	}
	// the partition plane is crossed here
	if side == P_DivlineSide(tls, t2x, t2y, bsp) {
		// the line doesn't touch the other side
		return 1
	}
	// cross the ending side
	return P_CrossBSPNode(tls, libc.Int32FromUint16(*(*uint16)(unsafe.Pointer(bsp + 48 + uintptr(side^int32(1))*2))))
}

// C documentation
//
//	//
//	// P_CheckSight
//	// Returns true
//	//  if a straight line between t1 and t2 is unobstructed.
//	// Uses REJECT.
//	//
func P_CheckSight(tls *libc.TLS, t1 uintptr, t2 uintptr) (r boolean) {
	var bitnum, bytenum, pnum, s1, s2 int32
	// First check for trivial rejection.
	// Determine subsector entries in REJECT table.
	s1 = sectorIndex(((*mobj_t)(unsafe.Pointer(t1)).Fsubsector).Fsector)
	s2 = sectorIndex(((*mobj_t)(unsafe.Pointer(t2)).Fsubsector).Fsector)
	pnum = s1*numsectors + s2
	bytenum = pnum >> 3
	bitnum = 1 << (pnum & 7)
	// Check in REJECT table.
	if libc.Int32FromUint8(*(*uint8)(unsafe.Pointer(rejectmatrix + uintptr(bytenum))))&bitnum != 0 {
		sightcounts[0]++
		// can't possibly be connected
		return 0
	}
	// An unobstructed LOS is possible.
	// Now look from eyes of t1 to any part of t2.
	sightcounts[int32(1)]++
	validcount++
	sightzstart = (*mobj_t)(unsafe.Pointer(t1)).Fz + (*mobj_t)(unsafe.Pointer(t1)).Fheight - (*mobj_t)(unsafe.Pointer(t1)).Fheight>>2
	topslope = (*mobj_t)(unsafe.Pointer(t2)).Fz + (*mobj_t)(unsafe.Pointer(t2)).Fheight - sightzstart
	bottomslope = (*mobj_t)(unsafe.Pointer(t2)).Fz - sightzstart
	strace.Fx = (*mobj_t)(unsafe.Pointer(t1)).Fx
	strace.Fy = (*mobj_t)(unsafe.Pointer(t1)).Fy
	t2x = (*mobj_t)(unsafe.Pointer(t2)).Fx
	t2y = (*mobj_t)(unsafe.Pointer(t2)).Fy
	strace.Fdx = (*mobj_t)(unsafe.Pointer(t2)).Fx - (*mobj_t)(unsafe.Pointer(t1)).Fx
	strace.Fdy = (*mobj_t)(unsafe.Pointer(t2)).Fy - (*mobj_t)(unsafe.Pointer(t1)).Fy
	// the head node is the last node output
	return P_CrossBSPNode(tls, numnodes-1)
}

