func P_ShootSpecialLine(tls *libc.TLS, thing uintptr, line *line_t) {
	var ok int32
	//	Impacts that other things can activate.
	if !((*mobj_t)(unsafe.Pointer(thing)).Fplayer != nil) {
		ok = 0
		switch int32(line.Fspecial) {
		case 46:
			// OPEN DOOR IMPACT
			ok = 1
			break
		}
		if !(ok != 0) {
			return
		}
	}
	switch int32(line.Fspecial) {
	case 24:
		// RAISE FLOOR
		EV_DoFloor(tls, line, int32(raiseFloor))
		P_ChangeSwitchTexture(tls, line, 0)
	case 46:
		// OPEN DOOR
		EV_DoDoor(tls, line, int32(vld_open))
		P_ChangeSwitchTexture(tls, line, 1)
	case 47:
		// RAISE FLOOR NEAR AND CHANGE
		EV_DoPlat(tls, line, int32(raiseToNearestAndChange), 0)
		P_ChangeSwitchTexture(tls, line, 0)
		break
	}
}

// C documentation
//
//	//
//	// P_PlayerInSpecialSector
//	// Called every tic frame
//	//  that the player origin is in a special sector
//	//
func P_PlayerInSpecialSector(tls *libc.TLS, player *player_t) {
	var sector *sector_t
	sector = ((*mobj_t)(unsafe.Pointer(player.Fmo)).Fsubsector).Fsector
	// Falling, not all the way down yet?
	if (*mobj_t)(unsafe.Pointer(player.Fmo)).Fz != sector.Ffloorheight {
		return
	}
	// Has hitten ground.
	switch int32(sector.Fspecial) {
	case 5:
		// HELLSLIME DAMAGE
		if !(player.Fpowers[pw_ironfeet] != 0) {
			if !(leveltime&0x1f != 0) {
				P_DamageMobj(tls, player.Fmo, uintptr(0), uintptr(0), 10)
			}
		}
	case 7:
		// NUKAGE DAMAGE
		if !(player.Fpowers[pw_ironfeet] != 0) {
			if !(leveltime&0x1f != 0) {
				P_DamageMobj(tls, player.Fmo, uintptr(0), uintptr(0), 5)
			}
		}
	case 16:
		// SUPER HELLSLIME DAMAGE
		fallthrough
	case 4:
		// STROBE HURT
		if !(player.Fpowers[pw_ironfeet] != 0) || P_Random() < 5 {
			if !(leveltime&0x1f != 0) {
				P_DamageMobj(tls, player.Fmo, uintptr(0), uintptr(0), 20)
			}
		}
	case 9:
		// SECRET SECTOR
		player.Fsecretcount++
		sector.Fspecial = 0
	case 11:
		// EXIT SUPER DAMAGE! (for E1M8 finale)
		player.Fcheats &= ^int32(CF_GODMODE)
		if !(leveltime&0x1f != 0) {
			P_DamageMobj(tls, player.Fmo, uintptr(0), uintptr(0), 20)
		}
		if player.Fhealth <= 10 {
			G_ExitLevel()
		}
	default:
		I_Error(tls, __ccgo_ts(25387), int32(sector.Fspecial))
		break
	}
}

func P_UpdateSpecials(tls *libc.TLS) {
	var i, pic int32
	//	LEVEL TIMER
	if levelTimer == 1 {
		levelTimeCount--
		if !(levelTimeCount != 0) {
			G_ExitLevel()
		}
	}
	//	ANIMATE FLATS AND TEXTURES GLOBALLY
	for pos := 0; ; pos++ {
		anim := &anims[pos]
		if anim == lastanim {
			break
		}
		i = anim.Fbasepic
		for {
			if !(i < anim.Fbasepic+anim.Fnumpics) {
				break
			}
			pic = anim.Fbasepic + (leveltime/anim.Fspeed+i)%anim.Fnumpics
			if anim.Fistexture != 0 {
				*(*int32)(unsafe.Pointer(texturetranslation + uintptr(i)*4)) = pic
			} else {
				*(*int32)(unsafe.Pointer(flattranslation + uintptr(i)*4)) = pic
			}
			i++
		}

	}
	//	ANIMATE LINE SPECIALS
	i = 0
	for {
		if !(i < int32(numlinespecials)) {
			break
		}
		line := linespeciallist[i]
		switch int32(line.Fspecial) {
		case 48:
			// EFFECT FIRSTCOL SCROLL +
			(*(*side_t)(unsafe.Pointer(sides + uintptr(line.Fsidenum[0])*24))).Ftextureoffset += 1 << FRACBITS
			break
		}
		goto _3
	_3:
		;
		i++
	}
	//	DO BUTTONS
	i = 0
	for {
		if !(i < int32(MAXBUTTONS)) {
			break
		}
		if buttonlist[i].Fbtimer != 0 {
			buttonlist[i].Fbtimer--
			if !(buttonlist[i].Fbtimer != 0) {
				switch buttonlist[i].Fwhere {
				case int32(top):
					(*(*side_t)(unsafe.Pointer(sides + uintptr(buttonlist[i].Fline.Fsidenum[0])*24))).Ftoptexture = int16(buttonlist[i].Fbtexture)
				case int32(middle):
					(*(*side_t)(unsafe.Pointer(sides + uintptr(buttonlist[i].Fline.Fsidenum[0])*24))).Fmidtexture = int16(buttonlist[i].Fbtexture)
				case int32(bottom):
					(*(*side_t)(unsafe.Pointer(sides + uintptr(buttonlist[i].Fline.Fsidenum[0])*24))).Fbottomtexture = int16(buttonlist[i].Fbtexture)
					break
				}
				S_StartSound(tls, uintptr(unsafe.Pointer(&buttonlist))+uintptr(i)*32+24, int32(sfx_swtchn))
				xmemset(uintptr(unsafe.Pointer(&buttonlist))+uintptr(i)*32, 0, uint64(32))
			}
		}
		goto _4
	_4:
		;
		i++
	}
}

//
// Donut overrun emulation
//
// Derived from the code from PrBoom+.  Thanks go to Andrey Budko (entryway)
// as usual :-)
//

func DonutOverrun(tls *libc.TLS, s3_floorheight uintptr, s3_floorpic uintptr, line *line_t, pillar_sector *sector_t) {
	var p int32
	if first != 0 {
		// This is the first time we have had an overrun.
		first = 0
		// Default values
		tmp_s3_floorheight = DONUT_FLOORHEIGHT_DEFAULT
		tmp_s3_floorpic = int32(DONUT_FLOORPIC_DEFAULT)
		//!
		// @category compat
		// @arg <x> <y>
		//
		// Use the specified magic values when emulating behavior caused
		// by memory overruns from improperly constructed donuts.
		// In Vanilla Doom this can differ depending on the operating
		// system.  The default (if this option is not specified) is to
		// emulate the behavior when running under Windows 98.
		p = M_CheckParmWithArgs(__ccgo_ts(25431), 2)
		if p > 0 {
			// Dump of needed memory: (fixed_t)0000:0000 and (short)0000:0008
			//
			// C:\>debug
			// -d 0:0
			//
			// DOS 6.22:
			// 0000:0000    (57 92 19 00) F4 06 70 00-(16 00)
			// DOS 7.1:
			// 0000:0000    (9E 0F C9 00) 65 04 70 00-(16 00)
			// Win98:
			// 0000:0000    (00 00 00 00) 65 04 70 00-(16 00)
			// DOSBox under XP:
			// 0000:0000    (00 00 00 F1) ?? ?? ?? 00-(07 00)
			M_StrToInt(tls, *(*uintptr)(unsafe.Pointer(myargv + uintptr(p+int32(1))*8)), &tmp_s3_floorheight)
			M_StrToInt(tls, *(*uintptr)(unsafe.Pointer(myargv + uintptr(p+int32(2))*8)), &tmp_s3_floorpic)
			if tmp_s3_floorpic >= numflats {
				fprintf_ccgo(os.Stderr, 25438, numflats, DONUT_FLOORPIC_DEFAULT)
				tmp_s3_floorpic = int32(DONUT_FLOORPIC_DEFAULT)
			}
		}
	}
	/*
	   fprintf(stderr,
	           "Linedef: %d; Sector: %d; "
	           "New floor height: %d; New floor pic: %d\n",
	           line->iLineID, pillar_sector->iSectorID,
	           tmp_s3_floorheight >> 16, tmp_s3_floorpic);
	*/
	*(*fixed_t)(unsafe.Pointer(s3_floorheight)) = tmp_s3_floorheight
	*(*int16)(unsafe.Pointer(s3_floorpic)) = int16(tmp_s3_floorpic)
}

var first = 1

var tmp_s3_floorheight int32

var tmp_s3_floorpic int32

// C documentation
//
//	//
//	// Special Stuff that can not be categorized
//	//
func EV_DoDonut(tls *libc.TLS, line *line_t) (r int32) {
	bp := alloc(16)
	var floor uintptr
	var s1, s2, s3 *sector_t
	var i, rtn, secnum, v1 int32
	secnum = -1
	rtn = 0
	for {
		v1 = P_FindSectorFromLineTag(tls, line, secnum)
		secnum = v1
		if !(v1 >= 0) {
			break
		}
		s1 = &sectors[secnum]
		// ALREADY MOVING?  IF SO, KEEP GOING...
		if (*sector_t)(unsafe.Pointer(s1)).Fspecialdata != 0 {
			continue
		}
		rtn = 1
		s2 = getNextSector(tls, s1.Flines[0], s1)
		// Vanilla Doom does not check if the linedef is one sided.  The
		// game does not crash, but reads invalid memory and causes the
		// sector floor to move "down" to some unknown height.
		// DOSbox prints a warning about an invalid memory access.
		//
		// I'm not sure exactly what invalid memory is being read.  This
		// isn't something that should be done, anyway.
		// Just print a warning and return.
		if s2 == nil {
			fprintf_ccgo(os.Stderr, 25590)
			break
		}
		i = 0
		for {
			if !(i < (*sector_t)(unsafe.Pointer(s2)).Flinecount) {
				break
			}
			s3 = s2.Flines[i].Fbacksector
			if s3 == s1 {
				goto _2
			}
			if s3 == nil {
				// e6y
				// s3 is NULL, so
				// s3->floorheight is an int at 0000:0000
				// s3->floorpic is a short at 0000:0008
				// Trying to emulate
				fprintf_ccgo(os.Stderr, 25682)
				DonutOverrun(tls, bp, bp+4, line, s1)
			} else {
				*(*fixed_t)(unsafe.Pointer(bp)) = (*sector_t)(unsafe.Pointer(s3)).Ffloorheight
				*(*int16)(unsafe.Pointer(bp + 4)) = (*sector_t)(unsafe.Pointer(s3)).Ffloorpic
			}
			//	Spawn rising slime
			floor = Z_Malloc(tls, 64, int32(PU_LEVSPEC), uintptr(0))
			P_AddThinker(tls, floor)
			(*sector_t)(unsafe.Pointer(s2)).Fspecialdata = floor
			*(*actionf_p1)(unsafe.Pointer(floor + 16)) = __ccgo_fp(T_MoveFloor)
			(*floormove_t)(unsafe.Pointer(floor)).Ftype1 = int32(donutRaise)
			(*floormove_t)(unsafe.Pointer(floor)).Fcrush = 0
			(*floormove_t)(unsafe.Pointer(floor)).Fdirection = 1
			(*floormove_t)(unsafe.Pointer(floor)).Fsector = s2
			(*floormove_t)(unsafe.Pointer(floor)).Fspeed = 1 << FRACBITS / 2
			(*floormove_t)(unsafe.Pointer(floor)).Ftexture = *(*int16)(unsafe.Pointer(bp + 4))
			(*floormove_t)(unsafe.Pointer(floor)).Fnewspecial = 0
			(*floormove_t)(unsafe.Pointer(floor)).Ffloordestheight = *(*fixed_t)(unsafe.Pointer(bp))
			//	Spawn lowering donut-hole
			floor = Z_Malloc(tls, 64, int32(PU_LEVSPEC), uintptr(0))
			P_AddThinker(tls, floor)
			(*sector_t)(unsafe.Pointer(s1)).Fspecialdata = floor
			*(*actionf_p1)(unsafe.Pointer(floor + 16)) = __ccgo_fp(T_MoveFloor)
			(*floormove_t)(unsafe.Pointer(floor)).Ftype1 = int32(lowerFloor)
			(*floormove_t)(unsafe.Pointer(floor)).Fcrush = 0
			(*floormove_t)(unsafe.Pointer(floor)).Fdirection = -1
			(*floormove_t)(unsafe.Pointer(floor)).Fsector = s1
			(*floormove_t)(unsafe.Pointer(floor)).Fspeed = 1 << FRACBITS / 2
			(*floormove_t)(unsafe.Pointer(floor)).Ffloordestheight = *(*fixed_t)(unsafe.Pointer(bp))
			break
			goto _2
		_2:
			;
			i++
		}
	}
	return rtn
}

// C documentation
//
//	// Parses command line parameters.
func P_SpawnSpecials(tls *libc.TLS) {
	var i int32
	// See if -TIMER was specified.
	if timelimit > 0 && deathmatch != 0 {
		levelTimer = 1
		levelTimeCount = timelimit * 60 * int32(TICRATE)
	} else {
		levelTimer = 0
	}
	//	Init special SECTORs.
	i = 0
	for {
		if !(i < numsectors) {
			break
		}
		sector := &sectors[i]
		if !(sector.Fspecial != 0) {
			goto _1
		}
		switch int32(sector.Fspecial) {
		case 1:
			// FLICKERING LIGHTS
			P_SpawnLightFlash(tls, sector)
		case 2:
			// STROBE FAST
			P_SpawnStrobeFlash(tls, sector, int32(FASTDARK), 0)
		case 3:
			// STROBE SLOW
			P_SpawnStrobeFlash(tls, sector, int32(SLOWDARK), 0)
		case 4:
			// STROBE FAST/DEATH SLIME
			P_SpawnStrobeFlash(tls, sector, int32(FASTDARK), 0)
			sector.Fspecial = 4
		case 8:
			// GLOWING LIGHT
			P_SpawnGlowingLight(tls, sector)
		case 9:
			// SECRET SECTOR
			totalsecret++
		case 10:
			// DOOR CLOSE IN 30 SECONDS
			P_SpawnDoorCloseIn30(tls, sector)
		case 12:
			// SYNC STROBE SLOW
			P_SpawnStrobeFlash(tls, sector, int32(SLOWDARK), 1)
		case 13:
			// SYNC STROBE FAST
			P_SpawnStrobeFlash(tls, sector, int32(FASTDARK), 1)
		case 14:
			// DOOR RAISE IN 5 MINUTES
			P_SpawnDoorRaiseIn5Mins(tls, sector, i)
		case 17:
			P_SpawnFireFlicker(tls, sector)
			break
		}
		goto _1
	_1:
		;
		i++
	}
	//	Init line EFFECTs
	numlinespecials = 0
	i = 0
	for {
		if !(i < numlines) {
			break
		}
		switch int32(lines[i].Fspecial) {
		case 48:
			if int32(numlinespecials) >= int32(MAXLINEANIMS) {
				I_Error(tls, __ccgo_ts(25801), 0)
			}
			// EFFECT FIRSTCOL SCROLL+
			linespeciallist[numlinespecials] = &lines[i]
			numlinespecials++
			break
		}
		goto _2
	_2:
		;
		i++
	}
	//	Init other misc stuff
	i = 0
	for {
		if !(i < int32(MAXCEILINGS)) {
			break
		}
		activeceilings[i] = nil
		goto _3
	_3:
		;
		i++
	}
	i = 0
	for {
		if !(i < int32(MAXPLATS)) {
			break
		}
		activeplats[i] = nil
		goto _4
	_4:
		;
		i++
	}
	i = 0
	for {
		if !(i < int32(MAXBUTTONS)) {
			break
		}
		xmemset(uintptr(unsafe.Pointer(&buttonlist))+uintptr(i)*32, 0, uint64(32))
		goto _5
	_5:
		;
		i++
	}
	// UNUSED: no horizonal sliders.
	//	P_InitSlidingDoorFrames();
}

