func S_Start(tls *libc.TLS) {
	var cnum, mnum int32
	var spmus [9]int32
	// kill all playing sounds at start of level
	//  (trust me - a good idea)
	cnum = 0
	for {
		if !(cnum < snd_channels) {
			break
		}
		if (*(*channel_t)(unsafe.Pointer(channels + uintptr(cnum)*24))).Fsfxinfo != nil {
			S_StopChannel(cnum)
		}
		goto _1
	_1:
		;
		cnum++
	}
	// start new music for the level
	mus_paused = uint32(0)
	if gamemode == commercial {
		mnum = int32(mus_runnin) + gamemap - 1
	} else {
		spmus = [9]int32{
			0: int32(mus_e3m4),
			1: int32(mus_e3m2),
			2: int32(mus_e3m3),
			3: int32(mus_e1m5),
			4: int32(mus_e2m7),
			5: int32(mus_e2m4),
			6: int32(mus_e2m6),
			7: int32(mus_e2m5),
			8: int32(mus_e1m9),
		}
		if gameepisode < 4 {
			mnum = int32(mus_e1m1) + (gameepisode-1)*int32(9) + gamemap - 1
		} else {
			mnum = spmus[gamemap-1]
		}
	}
	S_ChangeMusic(tls, mnum, 1)
}

