// Get the equivalent ASCII (Unicode?) character for a keypress.

func GetTypedChar(key uint8) (r uint8) {
	// Is shift held down?  If so, perform a translation.
	if shiftdown > 0 {
		if key >= 0 && key < 128 {
			key = shiftxform[key]
		} else {
			key = 0
		}
	}
	return key
}

func UpdateShiftStatus(pressed int32, key uint8) {
	var change int32
	if pressed != 0 {
		change = 1
	} else {
		change = -1
	}
	if key == 0x80+0x36 {
		shiftdown += change
	}
}

type DoomKeyEvent struct {
	Pressed bool
	Key     uint8
}

func I_GetEvent() {
	var bp event_t
	var event DoomKeyEvent
	for dg_frontend.GetKey(&event) {
		var pressed int32
		if event.Pressed {
			pressed = 1
		}

		UpdateShiftStatus(pressed, event.Key)
		// process event
		if event.Pressed {
			// data1 has the key pressed, data2 has the character
			// (shift-translated, etc)
			bp.Ftype1 = ev_keydown
			bp.Fdata1 = int32(event.Key)
			bp.Fdata2 = libc.Int32FromUint8(GetTypedChar(event.Key))
			if bp.Fdata1 != 0 {
				D_PostEvent(&bp)
			}
		} else {
			bp.Ftype1 = ev_keyup
			bp.Fdata1 = int32(event.Key)
			// data2 is just initialized to zero for ev_keyup.
			// For ev_keydown it's the shifted Unicode character
			// that was typed, but if something wants to detect
			// key releases it should do so based on data1
			// (key ID), not the printable char.
			bp.Fdata2 = 0
			if bp.Fdata1 != 0 {
				D_PostEvent(&bp)
			}
			break
		}
	}
	/*
	   case SDL_MOUSEMOTION:
	       event.type = ev_mouse;
	       event.data1 = mouse_button_state;
	       event.data2 = AccelerateMouse(sdlevent.motion.xrel);
	       event.data3 = -AccelerateMouse(sdlevent.motion.yrel);
	       D_PostEvent(&event);
	       break;
	*/
}

